import { ShoppingBag, Loader2 } from 'lucide-react';
import { useCart } from '@/context/cart-context';
import { addToCartAction } from '@/interface-adapters/actions/cart.actions';
import { Product } from '@/core/domain/types';

interface ProductActionsProps {
    product: Product;
}

export function ProductActions({ product }: ProductActionsProps) {
    const [isPending, startTransition] = useTransition();
    const { addItem, openCart } = useCart();

    // Default to first variant
    const firstVariant = product.variants?.[0];
    const sku = firstVariant?.sku || 'N/A';
    const variantId = firstVariant?.id || '';

    const handleAddToCart = () => {
        if (!variantId) {
            alert('Producto no disponible');
            return;
        }

        console.warn('ðŸ›’ Adding to cart:', { sku, variantId, productName: product.name });

        startTransition(async () => {
            const formData = new FormData();
            formData.append('variantId', sku); // Backend expects SKU
            formData.append('productId', product.id);
            formData.append('quantity', '1');

            const result = await addToCartAction(null, formData);
            console.log('âœ… Server Action result:', result);

            if (result.success) {
                addItem({
                    variantId,
                    productId: product.id,
                    productName: product.name,
                    sku,
                    quantity: 1,
                    unitPrice: product.basePrice,
                });
                console.log('âœ¨ addItem called in context');
                openCart();
            } else {
                alert(result.message || 'Error al aÃ±adir al carrito');
            }
        });
    };

    return (
        <div className="mt-8 flex gap-4">
            <button
                onClick={handleAddToCart}
                disabled={isPending || !variantId}
                className="flex-1 flex items-center justify-center gap-2 bg-blue-600 text-white rounded-lg py-4 px-6 font-semibold hover:bg-blue-700 transition-colors disabled:bg-gray-300 disabled:cursor-not-allowed"
            >
                {isPending ? (
                    <>
                        <Loader2 className="h-5 w-5 animate-spin" />
                        AÃ±adiendo...
                    </>
                ) : (
                    <>
                        <ShoppingBag className="h-5 w-5" />
                        AÃ±adir al Carrito
                    </>
                )}
            </button>
        </div>
    );
}

import { db } from '../database/connection';
import {
    customers,
    orders,
    orderItems,
    productVariants,
    products,
    inventoryStock,
} from '../database/schema';
import { eq, sql } from 'drizzle-orm';
import {
    IOrderRepository,
    CustomerInput,
    OrderItemInput,
} from '@/core/repositories/order.repository';
import { Order, CartItem } from '@/core/domain/types';

import { NodePgDatabase } from 'drizzle-orm/node-postgres';
import * as schema from '../database/schema';

export class DrizzleOrderRepository implements IOrderRepository {
    constructor(private readonly database: NodePgDatabase<typeof schema> = db) { }

    async createOrder(
        customer: CustomerInput,
        items: OrderItemInput[],
        shippingAddress: string
    ): Promise<string> {
        return await this.database.transaction(async (tx) => {
            // 1. Find or create customer
            let customerId: number;

            const existingCustomer = await tx
                .select()
                .from(customers)
                .where(eq(customers.email, customer.email))
                .limit(1);

            if (existingCustomer.length > 0) {
                customerId = existingCustomer[0].id;
            } else {
                const [newCustomer] = await tx
                    .insert(customers)
                    .values({
                        email: customer.email,
                        firstName: customer.firstName,
                        lastName: customer.lastName,
                        phone: customer.phone,
                        address: customer.address,
                        city: customer.city,
                    })
                    .returning();
                customerId = newCustomer.id;
            }

            // 2. Fetch variant prices and calculate totals
            // IMPORTANT: variantId here is actually the SKU (for inventory lookup consistency)
            let subtotal = 0;
            const orderItemsData: Array<{
                variantId: number;
                productName: string;
                sku: string;
                quantity: number;
                unitPrice: string;
                totalPrice: string;
            }> = [];

            for (const item of items) {
                // Resolution Logic: handle both SKU and numeric ID
                let variant;
                if (isNaN(Number(item.variantId))) {
                    [variant] = await tx
                        .select({
                            id: productVariants.id,
                            sku: productVariants.sku,
                            priceOverride: productVariants.priceOverride,
                            productId: productVariants.productId,
                        })
                        .from(productVariants)
                        .where(eq(productVariants.sku, item.variantId));
                } else {
                    [variant] = await tx
                        .select({
                            id: productVariants.id,
                            sku: productVariants.sku,
                            priceOverride: productVariants.priceOverride,
                            productId: productVariants.productId,
                        })
                        .from(productVariants)
                        .where(eq(productVariants.id, Number(item.variantId)));
                }

                if (!variant) {
                    throw new Error(`Variant with ID/SKU ${item.variantId} not found`);
                }

                // Get product for name and base price
                const [product] = await tx
                    .select({
                        name: products.name,
                        basePrice: products.basePrice,
                    })
                    .from(products)
                    .where(eq(products.id, variant.productId!));

                const unitPrice = variant.priceOverride
                    ? parseFloat(variant.priceOverride)
                    : parseFloat(product.basePrice);

                const totalPrice = unitPrice * item.quantity;
                subtotal += totalPrice;

                orderItemsData.push({
                    variantId: variant.id,
                    productName: product.name,
                    sku: variant.sku,
                    quantity: item.quantity,
                    unitPrice: unitPrice.toFixed(2),
                    totalPrice: totalPrice.toFixed(2),
                });
            }

            // 3. Create order
            const orderNumber = `ORD-${Date.now().toString(36).toUpperCase()}`;

            const [order] = await tx
                .insert(orders)
                .values({
                    orderNumber,
                    customerId,
                    status: 'PENDING',
                    paymentStatus: 'UNPAID',
                    subtotal: subtotal.toFixed(2),
                    taxTotal: '0',
                    shippingCost: '0',
                    totalAmount: subtotal.toFixed(2),
                    shippingAddress,
                })
                .returning();

            // 4. Create order items
            for (const itemData of orderItemsData) {
                await tx.insert(orderItems).values({
                    orderId: order.id,
                    ...itemData,
                });
            }

            return String(order.id);
        });
    }

    async findById(orderId: string): Promise<Order | null> {
        const result = await this.database
            .select()
            .from(orders)
            .where(eq(orders.id, parseInt(orderId)))
            .limit(1);

        if (result.length === 0) return null;

        const order = result[0];

        // Get order items
        const items = await this.database
            .select()
            .from(orderItems)
            .where(eq(orderItems.orderId, order.id));

        const cartItems: CartItem[] = items.map((item) => ({
            productId: String(item.variantId),
            variantId: String(item.variantId), // Mapped to varaintId properly? Check schema
            productName: item.productName,
            variantSku: item.sku,
            quantity: item.quantity,
            unitPrice: parseFloat(item.unitPrice),
        }));

        return {
            id: String(order.id),
            orderNumber: order.orderNumber,
            customerId: String(order.customerId),
            channel: 'WEB',
            status: order.status as Order['status'],
            paymentStatus: order.paymentStatus as Order['paymentStatus'],
            subtotal: parseFloat(order.subtotal),
            discountTotal: 0,
            taxTotal: parseFloat(order.taxTotal),
            shippingCost: parseFloat(order.shippingCost),
            grandTotal: parseFloat(order.totalAmount),
            items: cartItems,
            createdAt: order.createdAt ?? new Date(),
        };
    }

    async findByOrderNumber(orderNumber: string): Promise<Order | null> {
        const result = await this.database
            .select()
            .from(orders)
            .where(eq(orders.orderNumber, orderNumber))
            .limit(1);

        if (result.length === 0) return null;

        return this.findById(String(result[0].id));
    }

    async updateStatus(orderId: string, status: string): Promise<void> {
        await this.database
            .update(orders)
            .set({ status, updatedAt: new Date() })
            .where(eq(orders.id, parseInt(orderId)));
    }
}

import { describe, it, expect, beforeEach } from 'vitest';
import { renderHook, act } from '@testing-library/react';
import { CartProvider, useCart, CartItem } from '@/context/cart-context';
import React from 'react';

// Wrapper component for testing
const wrapper = ({ children }: { children: React.ReactNode }): React.JSX.Element => (
    <CartProvider>{children}</CartProvider>
);

describe('CartContext', (): void => {
    describe('Initial State', (): void => {
        it('should provide initial state with empty cart', (): void => {
            const { result } = renderHook(() => useCart(), { wrapper });

            expect(result.current.items).toEqual([]);
            expect(result.current.cartCount).toBe(0);
            expect(result.current.isOpen).toBe(false);
        });
    });

    describe('Cart Open/Close', (): void => {
        it('should open cart with openCart()', (): void => {
            const { result } = renderHook(() => useCart(), { wrapper });

            act((): void => {
                result.current.openCart();
            });

            expect(result.current.isOpen).toBe(true);
        });

        it('should close cart with closeCart()', (): void => {
            const { result } = renderHook(() => useCart(), { wrapper });

            act((): void => {
                result.current.openCart();
                result.current.closeCart();
            });

            expect(result.current.isOpen).toBe(false);
        });

        it('should toggle cart with toggleCart()', (): void => {
            const { result } = renderHook(() => useCart(), { wrapper });

            act((): void => {
                result.current.toggleCart();
            });
            expect(result.current.isOpen).toBe(true);

            act((): void => {
                result.current.toggleCart();
            });
            expect(result.current.isOpen).toBe(false);
        });
    });

    describe('Adding Items', (): void => {
        const testItem: CartItem = {
            variantId: 'NIKE-42',
            productId: 'prod-1',
            productName: 'Nike Air Max',
            sku: 'NIKE-42',
            quantity: 1,
            unitPrice: 459.9,
        };

        it('should add item to cart', (): void => {
            const { result } = renderHook(() => useCart(), { wrapper });

            act((): void => {
                result.current.addItem(testItem);
            });

            expect(result.current.items).toHaveLength(1);
            expect(result.current.items[0].productName).toBe('Nike Air Max');
        });

        it('should increment cartCount when adding item', (): void => {
            const { result } = renderHook(() => useCart(), { wrapper });

            act((): void => {
                result.current.addItem(testItem);
            });

            expect(result.current.cartCount).toBe(1);
        });

        it('should increment quantity for existing item', (): void => {
            const { result } = renderHook(() => useCart(), { wrapper });

            act((): void => {
                result.current.addItem(testItem);
                result.current.addItem(testItem);
            });

            expect(result.current.items).toHaveLength(1);
            expect(result.current.items[0].quantity).toBe(2);
            expect(result.current.cartCount).toBe(2);
        });

        it('should add different items separately', (): void => {
            const { result } = renderHook(() => useCart(), { wrapper });

            const secondItem: CartItem = {
                ...testItem,
                variantId: 'ADIDAS-42',
                sku: 'ADIDAS-42',
                productName: 'Adidas Ultraboost',
            };

            act((): void => {
                result.current.addItem(testItem);
                result.current.addItem(secondItem);
            });

            expect(result.current.items).toHaveLength(2);
            expect(result.current.cartCount).toBe(2);
        });
    });

    describe('Removing Items', (): void => {
        const testItem: CartItem = {
            variantId: 'NIKE-42',
            productId: 'prod-1',
            productName: 'Nike Air Max',
            sku: 'NIKE-42',
            quantity: 2,
            unitPrice: 459.9,
        };

        it('should remove item from cart', (): void => {
            const { result } = renderHook(() => useCart(), { wrapper });

            act((): void => {
                result.current.addItem(testItem);
                result.current.removeItem('NIKE-42');
            });

            expect(result.current.items).toHaveLength(0);
            expect(result.current.cartCount).toBe(0);
        });

        it('should not affect other items when removing', (): void => {
            const { result } = renderHook(() => useCart(), { wrapper });

            const secondItem: CartItem = {
                ...testItem,
                variantId: 'ADIDAS-42',
                sku: 'ADIDAS-42',
            };

            act((): void => {
                result.current.addItem(testItem);
                result.current.addItem(secondItem);
                result.current.removeItem('NIKE-42');
            });

            expect(result.current.items).toHaveLength(1);
            expect(result.current.items[0].variantId).toBe('ADIDAS-42');
        });
    });

    describe('Clear Cart', (): void => {
        it('should clear all items', (): void => {
            const { result } = renderHook(() => useCart(), { wrapper });

            act((): void => {
                result.current.addItem({
                    variantId: 'A',
                    productId: '1',
                    productName: 'A',
                    sku: 'A',
                    quantity: 1,
                    unitPrice: 100,
                });
                result.current.addItem({
                    variantId: 'B',
                    productId: '2',
                    productName: 'B',
                    sku: 'B',
                    quantity: 2,
                    unitPrice: 200,
                });
                result.current.clearCart();
            });

            expect(result.current.items).toEqual([]);
            expect(result.current.cartCount).toBe(0);
        });
    });

    describe('Error Handling', (): void => {
        it('should throw error when used outside provider', (): void => {
            expect((): void => {
                renderHook(() => useCart());
            }).toThrow('useCart must be used within a CartProvider');
        });
    });
});

